import React,{useState,useEffect} from 'react';
import './App.css';
import axios from 'axios';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";

function App() {


  return (
    <div className="App">
      <MainBody/>
    </div>
  );
}


//for main body
const MainBody = () => {
  const[poke, setPoke] = useState([])
  useEffect(()=>{
    axios.get('https://pokeapi.co/api/v2/pokemon')
    .then(res => {
      setPoke(res.data.results)
    //  console.log(res.data.results);
    })
  }, [])
  return(
    <div className="MainBody">
      {poke.map(item => (
        <div key={item.name}>
          <p>{item.name}</p>
          <DetailBody url={item.url}></DetailBody>
        </div>

      ))}
    </div>
  )
}

const DetailBody = props => {
  const[detail, setDetail] = useState([])

  useEffect(()=>{
    axios.get(props.url)
    .then(res =>{
      setDetail(res.data)
      console.log(res.data);
    })
    .catch(err => {
      console.log(err)
    })
  },[])

  return(
    <div>
  
    </div>
  )

}

export default App;
