import React,{useState,useEffect} from 'react';
import axios from 'axios';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";


const Detail = props => {
  const[detail, setDetail] = useState([])
  const[abiliti, setAbiliti] = useState([])
  useEffect(()=>{
    axios.get(props.url)
    .then(res =>{
      setDetail(res.data)
      setAbiliti(res.data.abilities)
      console.log(res.data)
    })
    .catch(err => {
      console.log(err)
    })
  }, [])

  return(
    <div>
      <Abilities abiliti={detail.abilities}/>
    </div>
  )
}


const Abilities = props =>{
  console.log(props.abiliti);
    return (
      <div>
        {props.map((abs)=>
          <p>{abs.ability}</p>
        )}
      </div>
    )

}


export default Detail
